select 

from bjwg_user ur
where ur.username = #{username,jdbcType=VARCHAR}

    SELECT  O.*,O.NUM as extend1,UR.NICKNAME,UR.HEAD_IMG,UR.USERNAME as username FROM(
		SELECT SUM(O.NUM) AS NUM, O.USER_ID FROM bjwg_order O
		WHERE O.RELATION_ID = #{relationId}
		GROUP BY O.USER_ID
	) AS O
	inner JOIN bjwg_user UR
	ON O.USER_ID = UR.USER_ID
	ORDER BY O.NUM DESC
    
      limit #{page.currentRow} , #{page.perRows}

  	select distinct  ,a.status as add_status,a.SEND_REMARK ,mer.TOTAL_NUM from bjwg_user ur
	RIGHT JOIN (
			SELECT DISTINCT a.* FROM (		
				SELECT MY_USER_ID AS USER_ID,STATUS,SEND_REMARK FROM bjwg_freinds 
				WHERE FREIND_ID = #{userId} 
				
			 	    AND status IN
			 	    
						#{tag}
					
			 	
				
			      	AND (status = 0 or AGREE_TIME >= #{agreeTime,jdbcType=TIMESTAMP})
			    
			    
				UNION ALL
				
				SELECT FREIND_ID AS USER_ID,(case when STATUS = 0 then 4 else STATUS end) STATUS,SEND_REMARK FROM bjwg_freinds 
				WHERE MY_USER_ID = #{userId} 
				
			 	    AND STATUS IN
			 	    
						#{tag}
					
			 	
				
			      	AND (status = 0 or AGREE_TIME >= #{agreeTime,jdbcType=TIMESTAMP})
			    
			) a
	) a
	ON ur.USER_ID = a.USER_ID
	left JOIN 
	(
		SELECT sum(NUM) as TOTAL_NUM,USER_ID FROM bjwg_my_earnings
		GROUP BY USER_ID
	)mer
	on mer.USER_ID = ur.USER_ID
	WHERE ur.USER_ID != #{userId}

  	select count(0) from(
	  	select distinct  ,a.status as add_status,a.SEND_REMARK ,mer.TOTAL_NUM from bjwg_user ur
		RIGHT JOIN (
				SELECT DISTINCT a.* FROM (		
					SELECT MY_USER_ID AS USER_ID,STATUS,SEND_REMARK FROM bjwg_freinds 
					WHERE FREIND_ID = #{userId} 
					
				 	    AND status IN
				 	    
							#{tag}
						
				 	
					
				      	AND (status = 0 or AGREE_TIME >= #{agreeTime,jdbcType=TIMESTAMP})
				    
				    
					UNION ALL
					
					SELECT FREIND_ID AS USER_ID,(case when STATUS = 0 then 4 else STATUS end) STATUS,SEND_REMARK FROM bjwg_freinds 
					WHERE MY_USER_ID = #{userId} 
					
				 	    AND STATUS IN
				 	    
							#{tag}
						
				 	
					
				      	AND (status = 0 or AGREE_TIME >= #{agreeTime,jdbcType=TIMESTAMP})
				    
				) a
		) a
		ON ur.USER_ID = a.USER_ID
		left JOIN 
		(
			SELECT sum(NUM) as TOTAL_NUM,USER_ID FROM bjwg_my_earnings
			GROUP BY USER_ID
		)mer
		on mer.USER_ID = ur.USER_ID
		WHERE ur.USER_ID != #{userId}
